import 'package:get/get.dart';

class CambioImagenController extends GetxController {
  int indexUrlImg = 0;
  List<String> urlImgs = [
    'https://image.made-in-china.com/2f0j00mspYTtNJfgbF/Family-Use-5-Doors-7-Seater-FF-Gasolina-Medium-Big-Space-SUV-Auto-Veiculos.jpg',
    'http://c.files.bbci.co.uk/6239/production/_94254152_gettyimages-97434768.jpg',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR7xYYn3SWk0AohZgNmp5rDRz5uTWIwZHvtRQ&usqp=CAU',
    'data:image/jpeg;base64,'
  ];

  void cambioImgLeft() {
    if (indexUrlImg == 0) {
      indexUrlImg = 4;
    } else {
      indexUrlImg--;
    }
    update();
  }

  void cambioImgRight() {
    if (indexUrlImg == 4) {
      indexUrlImg = 0;
    } else {
      indexUrlImg++;
    }
    update();
  }
}
